import java.util.Scanner;public class gcd{    public int gcd(int n1,int n2)    {       int gcd= 1;       int min= (n1<n2)?n1:n2;       for(int i=1;i<=min;i++)       {          if(n1%i==0 && n2%i==0)           gcd = i;       }       return gcd;    }    public static void main(String args[])    {       Scanner in = new Scanner(System.in);       System.out.println("Enter two numbers");       int n1= in.nextInt();       int n2= in.nextInt();       gcd a1=new gcd();       int x=a1.gcd(n1,n2);       a1.gcd(n1,n2);       System.out.println(x);    }}